pragma solidity ^0.6.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract BeyondToken is ERC20, Ownable {
    mapping(address => bool) private _lockedAccounts;

    event Locked(address target);

    function lockAccount(address target) public onlyOwner {
        _lockedAccounts[target] = true;
        emit Locked(target);
    }

    function unlockAccount(address target) public onlyOwner {
        _lockedAccounts[target] = false;
    }

    function isLocked(address target) public view returns (bool) {
        return _lockedAccounts[target];
    }

    constructor() public ERC20("DunkCoin231", "DK231") {
        _mint(msg.sender, 100 * (10**6) * (10**18));
    }

    function mint(uint256 amount) public onlyOwner {
        _mint(msg.sender, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(!_lockedAccounts[from], "account is locked");
    }
}
